import React, { useState, useRef, useEffect} from 'react';
function Songplayers({currentsong, setsongs, setcurrentsong, isplaying, setisplaying ,audioRef,  songinfo, setsonginfo, songs}){
useEffect(() => {
	const Newgana = songs.map((song)=>{
		if (song.id === currentsong.id){
			return{
				...song,
				Active:true,
			};
		}else{
				return{
					...song,
					Active:false,

				};
			}
			
		}
		
		
		);
		setsongs(Newgana);

}, [currentsong])
	
	const timehandler =(e)=>{
	const current= e.target.currentTime;
	 const duration =e.target.duration;
	setsonginfo({...songinfo, currentTime:current , duration})
	}
	
	const clickhandler =()=>{
		
	
		if(isplaying){
			audioRef.current.pause();
			setisplaying(!isplaying)
		}else{
			audioRef.current.play();
			setisplaying(!isplaying)
			
		}
		
	
	}
	const Mathcorrect =(e)=>{
		return(
		(Math.floor(e/60)) + ':' + ('0' + Math.floor(e%60)).slice(-2)
		)
		
	}
	const changehandle =(e) =>{
		audioRef.current.currentTime= e.target.value;
		setsonginfo({...songinfo, currentTime:e.target.value})
		
		
	}
	const skip_handle = (direction)=>{

		const currentIndex = songs.findIndex((song) => song.id === currentsong.id)
		if(direction == "S_forward"){
			setcurrentsong(songs[(currentIndex +1) % songs.length] );
		} else if(direction == "S_backward"){
				if((currentIndex-1) == -1){
					setcurrentsong(songs[songs.length -1])
					return;
				}
				setcurrentsong(songs[(currentIndex -1) % songs.length] );		

		}
		
		/*....................add.............................*/
		if(isplaying){
			const promise = audioRef.current.play();
			if(promise !== undefined)
			promise.then((audio)=>{
				audioRef.current.play();
			}
			
			
			)
		}
		




		 //.................remove.................................//
	



	}
	
	return(
	<div className='player'>
	<div className="time_control">
	
	<p>{Mathcorrect(songinfo.currentTime)}</p>
	
	<input type="range" min={0} max={songinfo.duration || 0} value={songinfo.currentTime} onChange={changehandle}></input>
	
	<p>{Mathcorrect(songinfo.duration)} </p>
	
	</div>
	<div className="play_control">
	<button onClick={()=> skip_handle('S_backward')}>S_backward</button>
	<button onClick={clickhandler}>play</button>
	
	<button onClick={()=> skip_handle('S_forward')}>S_forward</button>
	<audio onLoadedMetadata={timehandler} onTimeUpdate={timehandler} ref={audioRef} src={currentsong.Audio}></audio>
	</div>
	</div>
	)
	
	
	
}


export default Songplayers
