import React, { useState} from 'react';
import Twe from './Twe'
function Next({detail ,name , setname}){
		const [data, setdata]=useState([]);
		
		const Say = (e) => { 
		setname(e.target.value);
		const Entry = () =>{
			setdata([...data, setname]);
		};
	
		};
		
		
		const Check = () =>{
			console.log(data);
			console.log(name);
			
		};
		
		
		return(
		<div>
		<h1>{name}</h1>
		<textarea value={name} onChange={Say} cols="50" rows="5"></textarea>
		<button onClick={Entry} >Submit</button>
		<button onClick={Check} >console</button>
		<Twe/>
		<Twe/>
		
		</div>
		)
		}
export default Next;
