import React, { useState} from 'react';
import Twe from './Twe'
function Next({detail ,name , setname}){
		const [datas, setdatas]=useState([]);
		
		const Say = (e) => { 
		setname(e.target.value);
		
		};
		const Entry = (e) =>{
			e.preventDefault();
			setdatas([...datas, name]);
			setname('');
			console.log(datas);
		};
		
		return(
		<div>
		<form onSubmit={Entry}>
		<h1>{name}</h1>
		<textarea value={name} onChange={Say} cols="50" rows="5"></textarea>
		<button>Submit</button>
		</form>{
		datas.map((data)=>(
<Twe detail={detail} data={data} datas={datas} setdatas={setdatas}/>))}
		</div>
		)
		}
export default Next;
