import React, { useState} from 'react';
import Twe from './Twe'
function Next({detail ,name , setname}){
		const [data, setdata]=useState([]);
		
		const Say = (e) => { 
		setname(e.target.value);
		
		};
		const Entry = (e) =>{
			e.preventDefault();
			console.log('hey');
			//setdata([...data, setname]);
		};
		
		return(
		<div>
		<form onSubmit={Entry}>
		<h1>{name}</h1>
		<textarea value={name} onChange={Say} cols="50" rows="5"></textarea>
		<button  >Submit</button>
		</form>
		<Twe/>
		<Twe/>
		
		</div>
		)
		}
export default Next;
