import React, {useState} from "react";
  import Todo from './Todo'
  import Submit from './Submit'
  import Hey from './Hey'
function App (){
	const [data, setdata] = useState(
	[
	{content:"buy food", id:1},
	{content:"buy cloths", id:2},
	{content:"play game", id:3}
	]
	);
	
	const adding =(add) =>{
		add.id = Math.random();
		const newlist = [...data, add];
		setdata(newlist);
		
	}
	const deletee =(e) =>{
		const newnewlist = data.filter((daa)=>{
		return(
			daa.id!==e
			
		)
		}
		
		
		)
		 setdata(newnewlist);
	}
	
	
	
	
		return(
		<div>
		<Hey />
		<h1> Todo's </h1>
		<Todo data={data} deletee={deletee}/>
		<Submit adding={adding}/>
		</div>
		
		)
		
		
		
	}
	
	




 export default App
