import React, {useState} from 'react'

function App(){
	const [finaldata, setfinaldata]= useState();
	const adding =(dataa)=>{
		console.log(dataa)
		
	}
	
	
	//......................................................SUBMIT...............................................................................
function Submit({adding}){

	const [content, setcontent] = useState('');
	const handlechange = (e) =>{
		setcontent(e.target.value);
	}

	const handlesubmit = (e) =>{
		let data = { firstinfo :content };
	    
		e.preventDefault();
		adding(data);
		setcontent('');
		
	}
	return(
	<div>
<form  onSubmit={handlesubmit}>
<input value={content} type="text" onChange={handlechange} ></input>
	<button>Submitt</button>
	</form>
	</div>
	)
}
	
	
	
	
	return(
	<>
	
	<Submit adding={adding} />
	</>
	)
	
	
}
export default App
