import React, { useState} from 'react';
import Todo from "./Todo" 
function App(){
		const [inputItems, setinputItems]= useState('');
		const [outputItems, setoutputItems]= useState([]);
	function Pagal(e){
		setinputItems(e.target.value)
	}
	function Click(){
		
		setoutputItems((oldtems)=>{
			return [...oldtems, inputItems];
			});
		setinputItems("");
	}
	function deleteItems(id){
		setoutputItems((oldtems)=>{
			return oldtems.filter((arrrr, index)=>{
				return index!==id;
				
			})
			
		})
	}

	return(
	
	<div className="IoDoitems_container">
	<div className="main_IoDoitems_container">
	<h1>TO DO LIST</h1>
	<div className="IoDoitems_input">
	<input type="text" placeholder="tems" onChange={Pagal} value={inputItems} />
	<button onClick={Click}> + </button>
	</div>
	<ol>
	{outputItems.map((items, index)=>{
		
		return(
		<Todo key={index} id={index} text={items} onSelect={deleteItems}/>
		)
		  
		
	})}
	</ol>
	</div>
	</div>
	
	)
}
export default App;
