	const [finaldata, setfinaldata]= useState([]);
	const adding =(dataa)=>{
		dataa.id = Math.random();
		const newlist =[...finaldata, dataa];
		setfinaldata(newlist);
	}
	
	const deleteitems =(id)=>{
		const newnewlist = finaldata.filter((da)=>{
			return(
			da.id!==id
			)
			
		})
		
		setfinaldata(newnewlist)
		
	}
	
