import React, { useState} from 'react';
function App(){
		const [inputItems, setinputItems]= useState('');
		const [outputItems, setoutputItems]= useState([]);
	function Pagal(e){
		setinputItems(e.target.value)
	}
	function Click(){
		
		setoutputItems((oldtems)=>{
			return [...oldtems, inputItems];	
		}
		);
	}
	function Loop(items){
		return<li>{items}</li>
		
	}

	return(
	
	<div className="IoDoitems_container">
	<div className="main_IoDoitems_container">
	<h1>TO DO LIST</h1>
	<div className="IoDoitems_input">
	<input type="text" placeholder="tems" onChange={Pagal} />
	<button onClick={Click}> + </button>
	</div>
	<ol>
	{outputItems.map(Loop)}
	</ol>
	</div>
	</div>
	
	)
}
export default App;
