import React, {useState} from 'react'

function App(){
	const [finaldata, setfinaldata]= useState([]);
	const adding =(dataa)=>{
		dataa.id = Math.random();
		const newlist =[...finaldata, dataa];
		setfinaldata(newlist);
	}
	
	
	//......................................................SUBMIT...............................................................................
function Submit({adding}){

	const [content, setcontent] = useState('');
	const handlechange = (e) =>{
		setcontent(e.target.value);
	}

	const handlesubmit = (e) =>{
		e.preventDefault();
		let data = { firstinfo :content };
		adding(data);
		setcontent('');
		
	}
	return(
	<div>
<form  onSubmit={handlesubmit}>
<input value={content} type="text" onChange={handlechange} ></input>
	<button>Submitt</button>
	</form>
	</div>
	)
}
//............................................................TODO...................................................................................
function Todo({daaa}){
	
		return(
	
	<h1  >{daaa.firstinfo}</h1>
	
	)
	
	
	
	
}
	
	
	
	return(
	<>{
		finaldata.map((daaa)=>{
			return(
	<Todo key={daaa.id} daaa={daaa}/>
		)})
	}
	<Submit adding={adding}  />
	</>
	)
	
	
}
export default App
