import React, {useEffect }from "react";


const Songlibrary =({song,  setcurrentsong ,isplaying, setisplaying ,audioRef  }) =>{
	const switchhandler =() =>{
		 setcurrentsong(song);
		if(isplaying){
			const promise = audioRef.current.play();
			console.log(promise)
		}
		 
	}
	
	
	
	return(
	
	<div className="library_song" onClick={switchhandler}>
	
<img src={song.Image} />
<div>
	<h2>{song.Music_name}</h2>
	<h2>{song.Artist_name}</h2> 
	</div>
	</div>
	
	)
		
		
	
}
export default Songlibrary
