import React, {useState} from "react";
  import Todo from './Todo'
  import Submit from './Submit'
function App (){
	const [data, setdata] = useState(
	['buy food', 'dance']
	);
	
	const adding =(add) =>{
		const newlist = [...data, add];
		setdata(newlist);
		
	}
	const deletee =(e) =>{
		const newnewlist = data.filter((daa)=>{
		return(
			daa.id!==e)
		}
		
		
		)
		 setdata(newnewlist);
	}
	
	
	
	
		return(
		<div>
		<h1> Todo's </h1>
		<Todo data={data} deletee={deletee}/>
		<Submit adding={adding}/>
		</div>
		
		)
		
		
		
	}
	
	




 export default App
