import React, { useState} from 'react';

function App({detail}){
		const [name, setname]= useState("Aakash");
		const Say = () => { 
		
		setname(detail[1]);
		};
		
		return(
		<div>
		<h1>hey {name}</h1>
		<textarea cols="50" rows="5"></textarea>
		<button onClick={Say}>reapot</button>
		</div>
		)
		}
export default App;
