import React, { useState} from 'react';
function App(){
	let [Name,setName]=useState('');
	let [fullname, setfullname]= useState('');
	let [Last, setLast] =useState('');
	let [Lastname, setLastname] =useState('');
	function Change(e){
		console.log(e.target.value);
		setName(e.target.value);
	}
	function Change2(e){
		
		setLast(e.target.value);
	}
	function fullnamefuncton(e){
		e.preventDefault();
		setfullname(Name);
		setLastname(Last);
	}

	
	return(
		<>
			<h1>hey, {fullname + ' '}{Lastname} </h1>
			<form onSubmit={fullnamefuncton}>
			<input type="text" placeholder='enter your name' onChange={Change} value={Name} />
			<input type="text" placeholder='enter your last name' onChange={Change2} value={Last} />
			<button type="submit" >click me</button>
			</form>
		</>
	)
	
}

export default App;
