import React, {useState, useRef } from "react";
  import Song from './Song'
  import Songplayers from './file/Songplayers'
  import Songcollection from './file/Songcollection'
  import Songlibrary from './file/Songlibrary'
  import Data from './Data'

function App (){
		const [songs, setsongs]= useState(Data());
		const [currentsong, setcurrentsong] = useState(songs[0]);
		const [isplaying, setisplaying] = useState(false);
		const audioRef = useRef(null);
		const [songinfo, setsonginfo ] = useState(
		{
		currentTime: null,
		duration:null,
		
		}
	
		)
		return(
		< div className="app">
		
		<Song currentsong={currentsong}  />
		
		<Songplayers audioRef ={audioRef }  songinfo={songinfo} setsonginfo={setsonginfo} isplaying={isplaying} setisplaying={setisplaying} currentsong={currentsong} />
		<Songcollection audioRef ={audioRef }  setcurrentsong={ setcurrentsong} isplaying={isplaying} setisplaying={setisplaying}  songs={songs} />
		</div>
		
		)
		
		
		
	}
	
	




 export default App
