import React, { useState} from 'react';
import Twe from './Twe'
function Next({detail ,name , setname}){
		const [data, setdata]=usesState(['hey']);
		const Entry = () =>{
			setdata([...data, setname]);
		};
	
		const Say = (e) => { 
		setname(e.target.value);
		};
		const Empty = () =>{
			setname("");
		}
		
		
		
		return(
		<div>
		<h1>{name}</h1>
		<textarea onChange={Say} cols="50" rows="5"></textarea>
		<button onClick={Entry} >submit</button>
		<Twe/>
		<Twe/>
		</div>
		)
		}
export default Next;
