import React, { useState, useRef} from 'react';
import Audio from './Death_Note.webm'
function Songplayers({currentsong, isplaying, setisplaying }){
	
	const audioRef = useRef(null);
	const [songinfo, setsonginfo ] = useState(
	{
		currentTime: null,
		duration:null,
		
	}
	
	)
	const timehandler =(e)=>{
	const current= e.target.currentTime;
	 const duration =e.target.duration;
	setsonginfo({...songinfo, currentTime:current , duration})
	
	}
	
	const clickhandler =()=>{
		
	
		if(isplaying){
			audioRef.current.pause();
			setisplaying(!isplaying)
		}else{
			audioRef.current.play();
			setisplaying(!isplaying)
			
		}
		
	
	}
	
	return(
	<div className='player'>
	<div className="time_control">
	
	<p>{songinfo.currentTime}</p>
	
	<input type="range" ></input>
	
	<p>{songinfo.duration} </p>
	
	</div>
	<div className="play_control">
	<button >S_backward</button>
	<button onClick={clickhandler}>play</button>
	
	<button >S_forward</button>
	<audio onTimeUpdate={timehandler} ref={audioRef} src={currentsong.Audio}></audio>
	</div>
	</div>
	)
	
	
	
}


export default Songplayers
