import React, { useState} from 'react';
function App(){
	let number=new Date().toLocaleTimeString();
	let [current, update] = useState(number);
	function loop(){
	number=new Date().toLocaleTimeString();
	update(number)
	}
	setInterval(Loop, 1000);
	return(
		<>
			<h1>{current}</h1>
			
		</>
	)
	
}

export default App;
