import React, { useState} from 'react';
function App(){
	let [first , setfirst ]= useState({
		firstname:'',
		lastname:'',
		emailname:'',
	});
	function onsubmits(e){
e.preventDefault();

}
	
	function Change(e){
	const value = e.target.value;
	const name = e.target.name;
		setfirst((pre)=>{
	
	if (name==="firstname"){
		return{
			firstname:value,
			lastname:pre.lastname,
			emailname:pre.emailname,
			}}
	else if(name=='lastname'){
			return{
				lastname:value,
				firstname:pre.firstname,
				emailname:pre.emailname,
				}}
				
	else if(name=='emailname'){
			return{
				lastname:pre.firstname,
				firstname:pre.firstname,
				emailname:value
				}}
		
	
		})
		}

	return(<>
	<h1>hey, {first.firstname+" "}{first.lastname}</h1>
			<form onSubmit={onsubmits}>
			<input type="text" placeholder='enter your name' onChange={Change}  name="firstname" />
			<input type="text" placeholder='enter your last name' onChange={Change}  name='lastname' />
			<input type="email" placeholder='enter your last name' onChange={Change}  name='emailname' />
			</form>
			</>
	)
}
export default App;
