import React, { useState} from 'react';



function App(){
	let number=0;
	let [current, update]=useState(number);
	function Num(){
		
		current=current+1;
		console.log(number);
		update(current);
	}
	
	
	
	
	
	return(
		<>
			<h1>{current}</h1>
			<button onClick={Num}>click me</button>
		</>
	)
	
}
export default App;
