import React, { useState} from 'react';
import Next from './Next'
function App({name , setname}){
		const Say = (e) => { 
		setname(e.target.value);
		};
		const Empty = () =>{
			setname("");
		}
		
		return(
		<div>
		<h1>hey {name}</h1>
		<textarea value={name} onChange={Say} cols="50" rows="5"></textarea>
		<button onClick={Empty}>empty</button>
		
		</div>
		)
		}
export default App;
