import React, { useState} from 'react';
function App(){
	let [first , setfirst ]= useState({
		firstname:'',
		lastname:'',
	});
	function Change(e){
	const value = e.target.value;
	const name = e.target.name;
		setfirst((pre)=>{
	
	if (name==="firstname"){
		return{
			firstname:value
		}
			}
		else if(e.target.name==='lastname'){
			return{
				lastname:value
			}
				
			}
			
			
		})
		}	
	

	
	return(<>
	<h1>hey, {first.firstname+" "}{first.lastname}</h1>
			
			<input type="text" placeholder='enter your name' onChange={Change} value={first.firstname} name="firstname" />
			<input type="text" placeholder='enter your last name' onChange={Change} value={first.lastname} name='lastname' />
			</>
	)
}
export default App;
