import React, { useState, useRef} from 'react';
import Audio from './Death_Note.webm'
function Songplayers({currentsong, isplaying, setisplaying }){
	
	const audioRef = useRef(null);
	const clickhandler =()=>{
		if(isplaying){
			audioRef.current.paush();
			setisplaying(!isplaying)
		}else{
			audioRef.current.play();
			setisplaying(!isplaying)
			
		}

		
	}
	
	return(
	<div className='player'>
	<div className="time_control">
	
	<p>start Time</p>
	
	<input type="range" ></input>
	
	<p>End Time </p>
	
	</div>
	<div className="play_control">
	<button >S_backward</button>
	<button onClick={clickhandler}>play</button>
	
	<button >S_forward</button>
	<audio ref={audioRef} src={currentsong.Audio}></audio>
	</div>
	</div>
	)
	
	
	
}


export default Songplayers
