import React, { useState} from 'react';
function App(){
	let [first , setfirst ]= useState({
		firstname:'',
		lastname:'',
	});
	function Change(e){
	const value = e.target.value;
	const name = e.target.name;
		setfirst((pre)=>{
	
	if (name==="firstname"){
		return{
			firstname:value,
			lastname:pre.lastname	}}
	else if(name=='lastname'){
			return{
				lastname:value,
				firstname:pre.firstname,}}
			
			
		})
		}	
	

	
	return(<>
	<h1>hey, {first.firstname+" "}{first.lastname}</h1>
			
			<input type="text" placeholder='enter your name' onChange={Change}  name="firstname" />
			<input type="text" placeholder='enter your last name' onChange={Change}  name='lastname' />
			</>
	)
}
export default App;
