import React from "react";
import ReactDOM from "react-dom";
import Head from "./Head";
import Main from "./Main";
import Foot from "./Foot";
import './index.css'

var date = new Date();
var hours = date.getHours()%12;
var greeting ='';
var color={};
if(hours>7){
	greeting='goodnight';
	color={color:'black'};
}
else if(hours<7 && hours>1){
	greeting='Goodafternoon';
	color={color:'yellow'};
}
else{
	greeting='Goodmorning'; 	
	color={color:'green'};
}


ReactDOM.render(
<div>
<h1 style={{color:'pink'}}>Hello sir,</h1><h1 style={color}>{greeting}</h1>
</div>
